mapper.select:
SELECT count(*) AS counts FROM system_msg_record a WHERE a.mobile = #{mobile}
AND a.msg_type = #{msgType}
AND DATE_FORMAT(a.create_time, '%Y-%m-%d') = DATE_FORMAT(NOW(), '%Y-%m-%d')

SELECT
a.msg_num
FROM
system_msg_record a
WHERE
a.mobile = #{mobile}

	and a.msg_type = #{msgType}

AND DATE_FORMAT(a.create_time, '%Y-%m-%d') = DATE_FORMAT(NOW(), '%Y-%m-%d')

	and TIMESTAMPDIFF(MINUTE,a.create_time,NOW()) < #{maxMinute}

ORDER BY a.id DESC LIMIT 0,1
